sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    //Check Category and Subcategory combo exists
    if (current.category && current.subcategory) {
        var incidentGr = new GlideRecord("incident");
        incidentGr.addEncodedQuery("category=" + current.category + "^subcategory=" + current.subcategory + "^sys_id!=" + current.sys_id + "^sys_created_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()");
        incidentGr.orderByDesc("sys_created_on");
        incidentGr.query();
        var problemId;
        if (incidentGr.next()) {
            if (incidentGr.problem_id) {
                problemId = incidentGr.problem_id;
            } else {
                var problemGr = new GlideRecord("problem");
                problemGr.newRecord();
                problemGr.short_description = "Incidents created with same Category and Subcategory";
                problemGr.insert();
                problemId = problemGr.sys_id;
                incidentGr.setValue("problem_id", problemId);
                incidentGr.update();
            }
            current.problem_id = problemId;
            current.setWorkflow(false);
            current.update();
            current.setWorkflow(true);
        }
    }

})(current, previous);
